d animated:animated];    // Configure the view for the selected state}- (void)setCollectionData:(Scene*)collectionData{    self.scene = collectionData;    [_containerCellView setCollectionData:collectionData];}-(void)insertItemInCollectionData:(Take*)item{    [_containerCellView insertItem:item];}-(void)addButtonAction:(id)sender{    UIButton *button = (UIButton*)sender;    NSLog(@"button tag: %i /n library index: %i ", button.tag, self.scene.libraryIndex);        [[NSNotificationCenter defaultCenter] postNotificationName:@"showVideoCamera" object:self.scene];    }@end